    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hôtel | Connexion{% endblock %}

{% block body %}
    <center>
        <br>
        <div class=\"container\">
            <div class=\"card text-center\">

                <form method=\"post\">

                    {% if app.user %}
                        <div class=\"mb-3\">
                            You are logged in as {{ app.user.username }}, <a href=\"{{ path('logout') }}\">Logout</a>
                        </div>
                    {% endif %}

                    <div class=\"card-header\">
                        Veuillez vous authentifier
                    </div>
                    <br>
                    <div class=\"form-group row\">
                        <label for=\"inputUsername\" class=\"col-sm-2 col-form-label\">Nom d'utilisateur</label>
                        <div class=\"col-sm-10\">
                            <input type=\"text\"
                                   value=\"{{ last_username }}\"
                                   name=\"username\"
                                   id=\"inputUsername\"
                                   class=\"form-control\"
                                   required autofocus>
                        </div>
                    </div>
                    <div class=\"form-group row\">
                        <label for=\"inputPassword\" class=\"col-sm-2 col-form-label\">Mot de passe</label>
                        <div class=\"col-sm-10\">
                            <input type=\"password\"
                                   name=\"password\"
                                   id=\"inputPassword\"
                                   class=\"form-control\"
                                   required>
                        </div>
                    </div>
                    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">
                    <button class=\"btn btn-success\" type=\"submit\">Se connecter</button>
                    {% if error %}
                        <br><br>
                        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                    {% endif %}
                </form>
            </div>
        </div>
    </center>
{% endblock %}
", "security/login.html.twig", "/var/www/html/matteoHotel/templates/security/login.html.twig");
    }
